import React, {useState} from 'react'
import { Link, useNavigate } from 'react-router-dom'
import Navbar from '../../COMPONENTS/Navbar/Navbar'
import './AuthPage.css'
const Login = () => {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
      });
    let navigate = useNavigate();
      const handleChange = (e) => {
        setFormData({...formData, [e.target.name]: e.target.value})
      };
    
      const handleSubmit = async (e) => {
        e.preventDefault();
        
        try {
          const response = await fetch("http://localhost:5000/api/loginuser",{
            method:'POST',
            headers:{
                'Content-Type':'application/json'
            },
            body:JSON.stringify({email: formData.email, password: formData.password})
          });
          const json =await response.json()
          console.log(json);
          if (json.success) {
            localStorage.setItem("userEmail", formData.email);
            localStorage.setItem("authToken", json.authToken);
            console.log(localStorage.getItem("authToken"))
            navigate('/');
          }
        } catch (error) {
          console.error('Error registering user:', error);
        }
      };
    return (
        
        <div className='authpage'>
            <Navbar reloadnavbar={false}/>

            <div className='authcont'>
                <img src='https://images.unsplash.com/photo-1495480137269-ff29bd0a695c?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1172&q=80'
                    alt='login' />

                <form className='authform'>
                    <h1>Login</h1>
                    <div className='formgroup'>
                        <label htmlFor='email'>Email</label>
                        <input type='email' id='email' name ='email' onChange={handleChange} value={formData.email} />
                    </div>

                    <div className='formgroup'>
                        <label htmlFor='password'>Password</label>
                        <input type='password' id='password' name ='password' onChange={handleChange} value={formData.password}/>
                    </div>

                    <Link to='/forgotpassword'
                        className='stylenone'
                    >
                        <p>Forgot password?</p>
                    </Link>
                    <Link to='/'
                        className='stylenone'
                        onClick={handleSubmit}
                    >
                        <button className='btn'>Login</button>
                    </Link>
                    <h2 className='or'>OR</h2>
                    <Link to='/signup'
                        className='stylenone'
                    >
                        <button className='btn'>Signup</button>
                    </Link>
                </form>
            </div>
        </div>
    )
}

export default Login
