// Import necessary libraries
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function VegetablePricePrediction() {
    const [vegetable, setVegetable] = useState('');
    const [month, setMonth] = useState('');
    const [estimatedPrice, setEstimatedPrice] = useState('');
    const [vegetableNames, setVegetableNames] = useState([]);

    const onClickedEstimatePrice = () => {
        // Make a POST request to predict_vegetable_price endpoint
        axios.post('/predict_vegetable_price', {
            vegetable,
            month: parseFloat(month)
        })
        .then(response => {
            setEstimatedPrice(response.data.estimated_price.toString() + ' Rupees');
        })
        .catch(error => {
            console.error('Error predicting vegetable price:', error);
        });
    };

    useEffect(() => {
        // Fetch vegetable names from Flask server when the component mounts
        axios.get('/get_vegetable_names')
            .then(response => {
                setVegetableNames(response.data.vegetables);
            })
            .catch(error => {
                console.error('Error fetching vegetable names:', error);
            });
    }, []);

    return (
        <div className="vegetable-price-prediction">
            <h1>Vegetable Price Prediction</h1>
            <div>
                <label>Select a Vegetable:</label>
                <select value={vegetable} onChange={(e) => setVegetable(e.target.value)}>
                    <option value="" disabled>Choose a Vegetable</option>
                    {vegetableNames.map(name => (
                        <option key={name} value={name}>{name}</option>
                    ))}
                </select>
            </div>
            <div>
                <label>Select a Month:</label>
                <input type="text" id="uiSqft" className="area" name="Squareft" value={month} onChange={(e) => setMonth(e.target.value)} />
            </div>
            <button className="submit" onClick={onClickedEstimatePrice} type="button">Estimate Price</button>
            {estimatedPrice && <div className="result"><h2>{estimatedPrice}</h2></div>}
        </div>
    );
}

export default VegetablePricePrediction;
